from selenium import webdriver
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.common.by import By
from selenium.webdriver.support.wait import WebDriverWait
import logging

# Add logging for local testing verification
logging.basicConfig(level=logging.INFO, format='%(message)s')
logger = logging.getLogger()
logger.addHandler(logging.FileHandler('test.log', 'a'))
print = logger.info
print("starting test log")

driver = webdriver.Chrome()
driver.get("https://www.python.org")

# Assert the title of the site is Python.org's title
print(driver.title)
if "Python" not in driver.title:
    raise Exception("Python home page was not loaded properly")

# Assert that we can find the Python Software Foundation link in the footer
python_foundation_url = driver.find_element(By.LINK_TEXT, "Python Software Foundation")

# Perform a search
search_field = driver.find_element(By.ID, "id-search-field")
search_field.clear()
search_field.send_keys("python tutorials for beginners")
search_field.send_keys(Keys.RETURN)

# We confirm the submission happened
print(driver.current_url)

# Perform a search that is too long for the URL bar
search_field = driver.find_element(By.ID, "id-search-field")
search_field.clear()
search_field.send_keys("Python    ")
search_field.send_keys(Keys.RETURN)
WebDriverWait(driver, 4)

# We confirm we got a bad request page
print(driver.current_url)
bad_request = driver.title
if "Bad Request" not in driver.title:
    raise Exception("Bad Request page was not loaded properly")
print(bad_request)

driver.close()

